import React, { useState, useEffect } from 'react';
import axios from 'axios';

const StatCard = ({ icon, title, value }) => (
  <div className="bg-white rounded-lg shadow-md p-6 flex items-center">
    <div className="text-3xl mr-4 text-primary-600">{icon}</div>
    <div>
      <p className="text-gray-600">{title}</p>
      <p className="text-2xl font-bold">{value}</p>
    </div>
  </div>
);

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalSales: 0,
    totalOrders: 0,
    totalUsers: 0,
    totalProducts: 0
  });
  const [salesData, setSalesData] = useState([]);

  useEffect(() => {
    fetchStats();
    fetchSalesData();
  }, []);

  const fetchStats = async () => {
    try {
      const res = await axios.get('/api/admin/stats');
      setStats(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const fetchSalesData = async () => {
    try {
      const res = await axios.get('/api/admin/sales-data');
      setSalesData(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold text-gray-800 mb-6">Dashboard</h1>
      
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <StatCard icon="ðŸ’°" title="Total Sales" value={`$${stats.totalSales.toLocaleString()}`} />
        <StatCard icon="ðŸ“¦" title="Total Orders" value={stats.totalOrders} />
        <StatCard icon="ðŸ‘¥" title="Total Users" value={stats.totalUsers} />
        <StatCard icon="ðŸ’Ž" title="Total Products" value={stats.totalProducts} />
      </div>
      
      <div className="bg-white rounded-lg shadow-md p-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-4">Sales Overview</h2>
        <div className="h-64">
          {/* Chart would be implemented here with a charting library */}
          <div className="flex items-center justify-center h-full text-gray-500">
            Sales chart visualization would appear here
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
