import React, { useState } from 'react';
import axios from 'axios';

const Chatbot = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([
    { text: "Hello! I'm your jewellery assistant. How can I help you today?", sender: 'bot' }
  ]);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    if (!input.trim()) return;

    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      const res = await axios.post('/api/chatbot', { message: input });
      const botMessage = { text: res.data.reply, sender: 'bot' };
      setMessages(prev => [...prev, botMessage]);
    } catch (err) {
      const errorMessage = { text: "Sorry, I'm having trouble responding. Please try again later.", sender: 'bot' };
      setMessages(prev => [...prev, errorMessage]);
    }
  };

  return (
    <>
      {isOpen && (
        <div className="fixed bottom-20 right-5 w-80 h-96 bg-white shadow-lg rounded-lg flex flex-col border border-gray-200 z-50">
          <div className="bg-primary-600 text-white p-3 rounded-t-lg">
            <h3 className="text-lg font-semibold">Jewellery Assistant</h3>
          </div>
          
          <div className="flex-1 overflow-y-auto p-3 space-y-2">
            {messages.map((msg, index) => (
              <div key={index} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
                <div className={`rounded-lg p-2 max-w-xs ${msg.sender === 'user' ? 'bg-primary-500 text-white' : 'bg-gray-200 text-gray-800'}`}>
                  {msg.text}
                </div>
              </div>
            ))}
          </div>
          
          <div className="p-3 border-t border-gray-200 flex">
            <input
              type="text"
              className="flex-1 border rounded-l-lg p-2 focus:outline-none focus:ring-2 focus:ring-primary-500"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleSend()}
              placeholder="Type your message..."
            />
            <button 
              onClick={handleSend}
              className="bg-primary-600 text-white rounded-r-lg px-4 hover:bg-primary-700 transition-colors duration-200"
            >
              Send
            </button>
          </div>
        </div>
      )}
      
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="fixed bottom-5 right-5 bg-primary-600 text-white rounded-full w-12 h-12 flex items-center justify-center shadow-lg hover:bg-primary-700 transition-colors duration-200 z-40"
      >
        {isOpen ? 'âœ•' : 'ðŸ’¬'}
      </button>
    </>
  );
};

export default Chatbot;
