import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';

const Navbar = () => {
  const { user, logout } = useAuth();

  return (
    <nav className="bg-white shadow-lg">
      <div className="max-w-6xl mx-auto px-4">
        <div className="flex justify-between">
          <div className="flex space-x-7">
            <div className="flex items-center py-4">
              <Link to="/" className="text-xl font-bold text-gray-800">Jewellery Store</Link>
            </div>
          </div>
          <div className="flex items-center space-x-3">
            <Link to="/" className="py-4 px-2 text-gray-500 font-semibold hover:text-gray-800 transition duration-300">Home</Link>
            <Link to="/products" className="py-4 px-2 text-gray-500 font-semibold hover:text-gray-800 transition duration-300">Products</Link>
            {user ? (
              <>
                <Link to="/cart" className="py-4 px-2 text-gray-500 font-semibold hover:text-gray-800 transition duration-300">Cart</Link>
                <button onClick={logout} className="py-2 px-4 bg-red-500 text-white rounded hover:bg-red-400 transition duration-300">Logout</button>
              </>
            ) : (
              <>
                <Link to="/login" className="py-2 px-4 bg-blue-500 text-white rounded hover:bg-blue-400 transition duration-300">Login</Link>
                <Link to="/register" className="py-2 px-4 bg-green-500 text-white rounded hover:bg-green-400 transition duration-300">Register</Link>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
