import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function Home() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [featuredCategories, setFeaturedCategories] = useState([
    { name: 'Rings', image: '💍', count: '120+ Items' },
    { name: 'Necklaces', image: '📿', count: '85+ Items' },
    { name: 'Earrings', image: '👂', count: '65+ Items' },
    { name: 'Bracelets', image: '📿', count: '45+ Items' },
  ]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('/api/products?limit=8');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
        // Mock data for demonstration
        setProducts([
          {
            _id: '1',
            name: 'Diamond Engagement Ring',
            price: 2999.99,
            image: 'https://images.unsplash.com/photo-1605100804763-247f67b3557e?w=400',
            category: 'Rings'
          },
          {
            _id: '2',
            name: 'Gold Pearl Necklace',
            price: 1599.50,
            image: 'https://images.unsplash.com/photo-1599643478518-a784e5dc4c8f?w=400',
            category: 'Necklaces'
          },
          {
            _id: '3',
            name: 'Silver Diamond Earrings',
            price: 899.99,
            image: 'https://images.unsplash.com/photo-1535632066927-ab7c9ab60908?w=400',
            category: 'Earrings'
          },
          {
            _id: '4',
            name: 'Platinum Wedding Band',
            price: 1299.99,
            image: 'https://images.unsplash.com/photo-1515562141207-7a88fb7ce338?w=400',
            category: 'Rings'
          }
        ]);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600"></div>
      </div>
    );
  }

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="bg-gradient-to-r from-purple-900 to-blue-800 text-white py-20 px-4">
        <div className="max-w-7xl mx-auto text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-4">
            Exquisite Jewellery Collection
          </h1>
          <p className="text-xl md:text-2xl mb-8 text-blue-100">
            Discover timeless pieces crafted with perfection
          </p>
          <Link 
            to="/products"
            className="inline-block bg-white text-blue-900 px-8 py-3 rounded-full font-semibold hover:bg-blue-50 transition duration-300 transform hover:scale-105"
          >
            Shop Now
          </Link>
        </div>
      </section>

      {/* Featured Categories */}
      <section className="py-16 px-4">
        <div className="max-w-7xl mx-auto">
          <h2 className="text-3xl font-bold text-center mb-12 text-gray-900">Shop by Category</h2>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-6">
            {featuredCategories.map((category, index) => (
              <Link
                key={index}
                to={`/products?category=${category.name.toLowerCase()}`}
                className="bg-white rounded-xl shadow-md p-6 text-center hover:shadow-lg transition duration-300 transform hover:scale-105"
              >
                <div className="text-4xl mb-3">{category.image}</div>
                <h3 className="font-semibold text-gray-900 mb-1">{category.name}</h3>
                <p className="text-sm text-gray-600">{category.count}</p>
              </Link>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products */}
      <section className="py-16 px-4 bg-gray-50">
        <div className="max-w-7xl mx-auto">
          <div className="flex justify-between items-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900">Featured Products</h2>
            <Link 
              to="/products"
              className="text-blue-600 hover:text-blue-800 font-semibold"
            >
              View All →
            </Link>
          </div>
          
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
            {products.map(product => (
              <div key={product._id} className="bg-white rounded-xl shadow-md overflow-hidden hover:shadow-lg transition duration-300">
                <div className="relative h-48 overflow-hidden">
                  <img 
                    src={product.image} 
                    alt={product.name}
                    className="w-full h-full object-cover hover:scale-110 transition duration-300"
                  />
                </div>
                <div className="p-4">
                  <p className="text-sm text-gray-600 mb-1">{product.category}</p>
                  <h3 className="font-semibold text-gray-900 mb-2 line-clamp-1">{product.name}</h3>
                  <div className="flex justify-between items-center">
                    <span className="text-xl font-bold text-blue-600">${product.price}</span>
                    <Link 
                      to={`/product/${product._id}`}
                      className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition duration-200"
                    >
                      View Details
                    </Link>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section className="py-16 px-4">
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="bg-blue-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-2xl">🚚</span>
              </div>
              <h3 className="font-semibold text-lg mb-2">Free Shipping</h3>
              <p className="text-gray-600">Free shipping on all orders over $100</p>
            </div>
            
            <div className="text-center">
              <div className="bg-blue-100 w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4">
                <span className="text-2xl">🔒</span>
              </div>
              <h3 className="font-semibold text-lg mb-2">Secure Payment</h3>
